mapper.insert:
INSERT INTO VUE_USERINFO(
                            EMAIL
                          , PW
                          , INPDATE
                        )
                VALUES(
                        #{email}
                      , #{password}
                      , #{inpDate}
                      )

mapper.select:
SELECT EMAIL
     , PW
  FROM VUE_USERINFO
 WHERE EMAIL = #{email} 
   AND PW = #{password}
