mapper.insert:
INSERT INTO VUE_VIDEO(
                      TITLE
                    , PATH
                    , TYPE
                    , VIDEO_NO
                    , INP_DT
                  )
          VALUES(
                  #{title}
                , #{path}
                , #{type}
                , #{video_no}
                , #{inp_dt}
)

mapper.select:
SELECT TITLE
     , PATH
     , TYPE
  FROM VUE_VIDEO
 WHERE TITLE = #{title} 
   AND TYPE = #{type}
